SELECT t_residential.residential_id, t_residential.residential_name, t_residential.address, t_area_code.province_code, t_area_code.province_name, t_area_code.city_code, t_area_code.city_name, t_area_code.district_code, t_area_code.district_name, t_area_code.village_code, t_area_code.village_name FROM t_residential LEFT JOIN t_area_code ON t_residential.village_code = t_area_code.village_code;